import React from 'react';
import {Link, useParams} from 'react-router-dom';
import {useDispatch, useSelector} from 'react-redux';
import {getDetails, getActivity} from '../Redux/action/index.js'
import { useEffect } from 'react';
import"./Detail.css";
import ActivityDetail from './ActivityDetail.jsx';


function Details() {

    const myCountry = useSelector(state => state.detail)
    const dispatch = useDispatch()

    const allActivity = useSelector((state)=>state.activity)
    const{id} = useParams()

    useEffect(()=>{
        dispatch(getDetails(id))

    },[dispatch])

    useEffect(()=>{
      dispatch(getActivity())
  },[dispatch])


  return (
    <>
    <div>
      <Link to="/home"  ><button  >volver</button></Link>
        </div>
    {
      myCountry.length>0 ?
      <div className='containerDetail'>
        <div className='imgContainer'>
        <img src={myCountry[0].flag} alt="" />
        </div>
        <div>
        <h1>nombre:{myCountry[0].name}</h1>
        <h2>codigo:{myCountry[0].id}</h2>
        <h2>capital:{myCountry[0].capital}</h2>
        <h2>continente:{myCountry[0].continents}</h2>
        <h2>subregion:{myCountry[0].subregion}</h2>
        <h2>area:{myCountry[0].area}</h2>
        <h2>poblacion:{myCountry[0].population}</h2> 
        </div>
        {/* {
          allActivity.length>0 ?
          <div>
          <h3>nombre:{allActivity[0].activity}</h3>
          </div>:
          <div>
            <Link to="/CreateActivity"><button  >Crear actividad</button></Link>
          </div>

        }       */}
        <ActivityDetail/>
      </div>: <p>loading</p>
    }   
    </>
  )
}
//to="/home" >Back to countries
export default Details
