import React from 'react';
import {Link, useParams} from 'react-router-dom';
import {useDispatch, useSelector} from 'react-redux';
import {getDetails, getActivity} from '../Redux/action/index.js'
import { useEffect } from 'react';
import"./Detail.css";
import ActivityDetail from './ActivityDetail.jsx';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

function Details() {

    const myCountry = useSelector(state => state.detail)
    const dispatch = useDispatch()

    const allActivity = useSelector((state)=>state.activity)
    const{id} = useParams()

    useEffect(()=>{
        dispatch(getDetails(id))

    },[dispatch])

    useEffect(()=>{
      dispatch(getActivity())
  },[dispatch])


  return (
    <>
    <div>

    </div>
    {
      myCountry.length>0 ?
      <div className='containerDetail'>

         <Card sx={{ Width: 450 }}>
      <CardMedia
        component="img"
        height="140"
        image={myCountry[0].flag}
        alt="green iguana"
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
        {myCountry[0].name}
        </Typography>
        <Typography variant="body1" color="text.secondary"  component="div">
        Codigo:{myCountry[0].id}
        <br />
        Capital:{myCountry[0].capital}
        <br />
        Continente:{myCountry[0].continents}
        <br />
        Subregion:{myCountry[0].subregion}
        <br />
        Area:{myCountry[0].area} Km2
        <br />
        Poblacion:{myCountry[0].population}
        </Typography>
      </CardContent>
      <CardActions>
      <ActivityDetail/>
        <Button size="small"></Button>
        <Link to="/home"  ><button  >volver</button></Link>
      </CardActions>
    </Card>
      </div>: <p>loading</p>


    }   
    </>
  )
}
//to="/home" >Back to countries
export default Details
