import { FILTER_ACTIVITY, GET_COUNTRY,GET_DETAILS_ID,GET_NAME_COUNTRY,ORDER_AZ,ORDER_CONTINENT,ORDER_POBLACION,GET_ACTIVITY} from '../action/index.js'

const initialState = {country:[], allCountry:[], mensaje: "", detail:[], activity:[], allActivities:[]}

function Reducer(state=initialState, action){
    switch (action.type) {
        case GET_COUNTRY:
            return{
                ...state,
                country: action.payload,
                allCountry:action.payload,
                allActivities:action.payload
            }
       case GET_ACTIVITY:
           return{
               ...state,
               activity:action.payload,
           }
        case GET_DETAILS_ID:
            return{
                ...state,
                detail:action.payload
            }
            case FILTER_BY_ACTIVITIY: {
                const countriesWithActivities = state.allCountries.filter(
                  (country) => country.activities.length > 0
                );
                console.log("paises con actividades", countriesWithActivities);
                let countriesSelected = [];
                countriesWithActivities.forEach((country) => {
                  country.activities.forEach((activity) => {
                    if (activity.name === action.payload) countriesSelected.push(country);
                  });
                });
                const countriesFiltered =
                  action.payload === "All"
                    ? state.allCountries
                    : countriesSelected
                console.log(countriesFiltered);
                return {
                  ...state,
                  countries: countriesFiltered,
                };
              }        
        case GET_NAME_COUNTRY:
            return{
                ...state,
                country:action.payload
            }
        case "BORRAR_ESTADO":
            return { ...state, mensaje: "" };
        case "CARGAR_ACTIVIDAD":
            return { ...state, mensaje: action.payload};

        case ORDER_AZ:
            const sortArr = action.payload === 'AtoZ'?
            state.country.sort(function(a, b){
                 if(a.name > b.name){return 1}
                if(b.name > a.name){return -1}
                 return 0
            }):
            state.country.sort(function(a, b){
                if(a.name > b.name){return -1}
                if(b.name > a.name){return 1}
                return 0
            })
            return{
                ...state,
                allCountry: sortArr
            }

        case ORDER_POBLACION:
                const sortWeigth = action.payload === 'asc'?
                state.country.sort(function(a, b){
                    if(Number(a.population) > Number(b.population)){return -1}
                    if(Number(b.population) > Number(a.population)){return 1}
                    return 0
                }):
                state.country.sort(function(a, b){
                    if(Number(a.population) > Number(b.population)){return 1}
                    if(Number(b.population) > Number(a.population)){return -1}
                    return 0
                })
                return{
                    ...state,
                    allCountry: sortWeigth
                }
        case ORDER_CONTINENT:
            const countries = state.allCountry
            const continentOrder = action.payload === 'all'? countries : countries.filter(e=>e.region === action.payload)
            return{
                ...state,
                country: continentOrder,
            }
        case FILTER_ACTIVITY:
            const allActivities = state.allActivities;
            const activityFilter = action.payload === 'All' ? allActivities.filter(e => e.activities.length > 0) :
                allActivities.filter(c => c.activities.find((element) => element.name.toLowerCase() === action.payload))
            return {
                ...state,
                country: activityFilter
            }

        default:
            return state;
    }

}

export default Reducer
