import React, { useEffect, useState } from 'react'
import {useSelector, useDispatch} from 'react-redux'
import Country from './Country'
import { Link } from 'react-router-dom'
import{getCountry, getActivity} from '../Redux/action/index.js'
import {orderAZ, orderPoblacion, orderContinen,filterActivity} from '../Redux/action'
import Paginado from './Paginado'
import SearchBar from './SearchBar'
import './Country.css'
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';



function Countrys() {

  const allActivity = useSelector((state)=>state.activity)
  console.log(allActivity)
  const allCountry = useSelector((state)=>state.country)
  // const allCountry = [{
  //   id:1,
  //   name:"Argentina",
  //   capital:"Bs.As",
  //   continent:"Sur America"
  // },
  // {
  //   id:2,
  //   name:"Italia",
  //   capital:"Roma",
  //   continent:"Europa"
  // },
  // {
  //   id:3,
  //   name:"Japon",
  //   capital:"Tokyo",
  //   continent:"Aisa"
  // }

//]
  const dispatch = useDispatch()

  const [orderAz, setOrderAz] = useState('')
  const [orderPopulation, setOrderPopulation] = useState('')
  const [orderContinent, setOrderContinent] = useState('')
  const [activity, setActivity] = useState('')

  const [currentPage, setCurrentPage] = useState(1)
  const [countryPerPage, setCountryPerPage] = useState(10)
  const indexLastCountry = currentPage * countryPerPage
  const indexFirstCountry = indexLastCountry - countryPerPage
  const currentCountry = allCountry.slice(indexFirstCountry,indexLastCountry)
  const paginado = (pageNumber)=>{
    setCurrentPage(pageNumber)
  }


    useEffect(()=>{
        dispatch(getCountry())
    },[dispatch])
    useEffect(()=>{
      dispatch(getActivity())
  },[dispatch])
    function handleOrderAz(e){
      e.preventDefault()
      dispatch(orderAZ(e.target.value))
      setCurrentPage(1)
      setOrderAz(e.target.value)
  }
  function handleOrderPopulation(e){
      e.preventDefault()
      dispatch(orderPoblacion(e.target.value))
      setCurrentPage(1)
      setOrderPopulation(e.target.value)
  }

  function handleContinent(e){
    e.preventDefault()
    dispatch(orderContinen(e.target.value))
    setCurrentPage(1)
    setOrderContinent(e.target.value)
  }

function handleFilterActivity(e){
  e.preventDefault()
  dispatch(filterActivity(e.target.value))
  setActivity(e.target.value)
}
function handleClick(e){
  e.preventDefault()
  dispatch(getCountry())
}
const continent =['america','africa','asia','oceania','europa']
  return (
    <>
     <nav className='containerNav'>
     <Link to = '/CreateActivity'> <button className='btnHome'>Crear Actividad </button></Link>
      <button  className='btnHome' onClick={e=>{handleClick(e)}}>Volver a cargar paises</button>
        <div>
          <select className='btnHome' onChange={(e)=>handleFilterActivity(e)} >
            <option  value="All">AllActivity</option>
            {allActivity?.map((a)=>(<option value={a.activity} key={a.id}>{a.activity}</option>))}
          </select> 
       </div>
        <div>
        <select  className='btnHome' onChange={(e)=>handleContinent(e)}>
            <option value="all">Seleccione Region</option>
            <option value="all">All</option>
            {continent?.map((c)=>(<option value={c}>{c}</option>))}
        </select>
        </div>

        <div>
        <select  className='btnHome' onChange={(e)=>handleOrderAz(e)} >
            <option value="">Orden Alfabetico </option>
            <option value="AtoZ">A-Z</option>
            <option value="ZtoA">Z-A</option>    
        </select>    
        </div>



        <div>

        <select className='btnHome' onChange={(e)=>handleOrderPopulation(e)}>
            <option value="">Orden por Poblacion </option>
            <option value="asc">Ascendente</option>
            <option value="desc">Descendente</option>
        </select>


        <Box sx={{ minWidth: 120 }}>
      <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">Orden por Poblacion</InputLabel>
        <Select
          labelId="demo-select-small"
          id="demo-select-small"
          value=""
          label="Age"
          onChange={handleOrderPopulation}
        >
          <MenuItem value="asc">Ascendente</MenuItem>
          <MenuItem value="desc">Descendente</MenuItem>
        </Select>
      </FormControl>
    </Box>

        </div>




        <SearchBar/>
      </nav>


    <div className='containerCard'> 
      {currentCountry.length > 0 ? currentCountry.map(e =>
        <Link className='link' key={e.id} to={`/country/${e.id}`}>
           <Country
                flag={e.flag}
               name={e.name}
               region={e.region}
               subregion={e.subregion}

           />
        </Link>
   ): 
   <h2>Loading</h2>}
   </div>
   <Paginado countryPerPage={countryPerPage} allCountry={allCountry.length} paginado={paginado}/>
   </>
  )
}

export default Countrys
