import React from 'react';
import {Link, useParams} from 'react-router-dom';
import {useDispatch, useSelector} from 'react-redux';
import {getDetails, getActivity} from '../Redux/action/index.js'
import { useEffect } from 'react';
import"./Detail.css";
import ActivityDetail from './ActivityDetail.jsx';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

function Details() {

    const myCountry = useSelector(state => state.detail)
    const dispatch = useDispatch()

    const allActivity = useSelector((state)=>state.activity)
    const{id} = useParams()

    useEffect(()=>{
        dispatch(getDetails(id))

    },[dispatch])

    useEffect(()=>{
      dispatch(getActivity())
  },[dispatch])


  return (
    <>
    <div>
      <Link to="/home"  ><button  >volver</button></Link>
    </div>
    {
      myCountry.length>0 ?
      <div className='containerDetail'>
        <div className='imgContainer'>
        <img src={myCountry[0].flag} alt="" />
        </div>
        <div>
        <h1>nombre:{myCountry[0].name}</h1>
        <h2>codigo:{myCountry[0].id}</h2>
        <h2>capital:{myCountry[0].capital}</h2>
        <h2>continente:{myCountry[0].continents}</h2>
        <h2>subregion:{myCountry[0].subregion}</h2>
        <h2>area:{myCountry[0].area}</h2>
        <h2>poblacion:{myCountry[0].population}</h2> 
        </div>
        {/* {
          allActivity.length>0 ?
          <div>
          <h3>nombre:{allActivity[0].activity}</h3>
          </div>:
          <div>
            <Link to="/CreateActivity"><button  >Crear actividad</button></Link>
          </div>

        }       */}
         <Card sx={{ maxWidth: 345 }}>
      <CardMedia
        component="img"
        height="140"
        image={myCountry[0].flag}
        alt="green iguana"
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          Nombre:  {myCountry[0].name}
        </Typography>
        <Typography variant="body2" color="text.secondary"  component="div">
        codigo:{myCountry[0].id}
        <br />
        capital:{myCountry[0].capital}
        <br />
        continente:{myCountry[0].continents}
        <br />
        subregion:{myCountry[0].subregion}
        <br />
        area:{myCountry[0].area}
        <br />
        poblacion:{myCountry[0].population}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small"></Button>
        <Button to="/home" size="small">Inicio</Button>
      </CardActions>
    </Card>
        <ActivityDetail/>
      </div>: <p>loading</p>


    }   
    </>
  )
}
//to="/home" >Back to countries
export default Details
